mapper.select:
SELECT shop_id shopId, -- 总订单量 COUNT(shop_id) orderNums, -- 总金额 SUM(total_price) totalPrices, -- 总配送金额 SUM(delivery_fee) deliveryFee FROM order_tb WHERE -- 所有订单都按支付时间走 payment_date is not null
AND shop_id IN
#{item}
AND DATE_FORMAT(payment_date, '%Y-%m-%d') = #{queryDate}
GROUP BY shop_id

SELECT shop_id shopId, -- 退款订单量 COUNT(shop_id) refundOrderNums, -- 退款总金额 SUM(total_price) refundTotalPrices, -- 退款配送金额 SUM(delivery_fee) refundDeliveryFee FROM order_tb WHERE -- 退款数据 `status`=10 AND payment_date is not null
AND shop_id IN
#{item}
AND DATE_FORMAT(payment_date, '%Y-%m-%d') = #{queryDate}
GROUP BY shop_id

SELECT account.`account_id` riderAccountId, account.`realname` riderName, account.`phone` phone, account.`school_id` schoolId, school.`name` schoolName, COUNT(account.account_id) orderNums FROM account_tb account LEFT JOIN order_tb o ON account.`account_id`=o.`rider_account_id` LEFT JOIN school_tb school ON account.`school_id`=school.`school_id` WHERE -- 骑手 account.role_id='1004' AND o.payment_date is not null
AND DATE_FORMAT(o.payment_date, '%Y-%m-%d') = #{queryDate}
AND account.realname LIKE CONCAT('%',#{queryRealName},'%')
AND account.phone LIKE CONCAT('%',#{queryPhone},'%')
AND account.`school_id`=#{querySchoolId}
GROUP BY account.`account_id` LIMIT #{offset},#{limit}

select count(1) from ( SELECT account.`account_id` riderAccountId, account.`realname` riderName, account.`phone` phone, account.`school_id` schoolId, school.`name` schoolName, COUNT(account.account_id) orderNums FROM account_tb account LEFT JOIN order_tb o ON account.`account_id`=o.`rider_account_id` LEFT JOIN school_tb school ON account.`school_id`=school.`school_id` WHERE -- 骑手 account.role_id='1004' AND o.payment_date is not null
AND DATE_FORMAT(o.payment_date, '%Y-%m-%d') = #{queryDate}
AND account.realname LIKE CONCAT('%',#{queryRealName},'%')
AND account.phone LIKE CONCAT('%',#{queryPhone},'%')
AND account.`school_id`=#{querySchoolId}
GROUP BY account.`account_id` ) temp
