mapper.select:
SELECT shop_id shopId, shop_name shopName, SUM(total_prices) totalPrices, SUM(refund_total_prices) refundTotalPrices, SUM(valid_total_prices) validTotalPrices, SUM(valid_delivery_fee) validDeliveryFee, SUM(shop_income) shopIncome, SUM(refund_ordernums) refundOrderNums, SUM(valid_ordernums) validOrderNums, SUM(delivery_ordernums) deliveryOrderNums, SUM(valid_coupon_money) validCouponMoney FROM shop_turnover_count_tb where canteen_id=#{canteenId}
AND DATE_FORMAT(count_date, '%Y-%m-%d') >= #{queryDate}
AND DATE_FORMAT(count_date, '%Y-%m-%d') <= #{queryEndDate}
group by shop_id LIMIT #{offset},#{limit}

select count(*) from (
    SELECT shop_id FROM shop_turnover_count_tb where canteen_id=#{canteenId}

            AND DATE_FORMAT(count_date, '%Y-%m-%d') >= #{queryDate}


            AND DATE_FORMAT(count_date, '%Y-%m-%d') <= #{queryEndDate}

    group by shop_id
)a

SELECT shop_id shopId, shop_name shopName, SUM(total_prices) totalPrices, SUM(refund_total_prices) refundTotalPrices, SUM(valid_total_prices) validTotalPrices, SUM(valid_delivery_fee) validDeliveryFee, SUM(shop_income) shopIncome, SUM(refund_ordernums) refundOrderNums, SUM(valid_ordernums) validOrderNums, SUM(delivery_ordernums) deliveryOrderNums, SUM(valid_coupon_money) validCouponMoney FROM shop_turnover_count_tb where canteen_id=#{canteenId}
AND DATE_FORMAT(count_date, '%Y-%m-%d') >= #{queryDate}
AND DATE_FORMAT(count_date, '%Y-%m-%d') <= #{queryEndDate}
